<div class="col-lg-4 col-sm-8 col-12 main-section text-center">

    <fieldset class="rating-star text-center pl-5 pb-5" data-id="{{$id}}" id="content-{{$id}}">
        <input type="radio" id="{{{$id}}}star5" name="rating" value="5" /><label class = "full" for="{{$id}}star5" title="Awesome - 5 stars"></label>
        <input type="radio" id="{{$id}}star4half" name="rating" value="4.5" /><label class="half" for="{{$id}}star4half" title="Pretty good - 4.5 stars"></label>
        <input type="radio" id="{{$id}}star4" name="rating" value="4" /><label class = "full" for="{{$id}}star4" title="Pretty good - 4 stars"></label>
        <input type="radio" id="{{$id}}star3half" name="rating" value="3.5" /><label class="half" for="{{$id}}star3half" title="Meh - 3.5 stars"></label>
        <input type="radio" id="{{$id}}star3" name="rating" value="3" /><label class = "full" for="{{$id}}star3" title="Meh - 3 stars"></label>
        <input type="radio" id="{{$id}}star2half" name="rating" value="2.5" /><label class="half" for="{{$id}}star2half" title="Kinda bad - 2.5 stars"></label>
        <input type="radio" id="{{$id}}star2" name="rating" value="2" /><label class = "full" for="{{$id}}star2" title="Kinda bad - 2 stars"></label>
        <input type="radio" id="{{$id}}star1half" name="rating" value="1.5" /><label class="half" for="{{$id}}star1half" title="Meh - 1.5 stars"></label>
        <input type="radio" id="{{$id}}star1" name="rating" value="1" /><label class = "full" for="{{$id}}star1" title="Sucks big time - 1 star"></label>
        <input type="radio" id="{{$id}}starhalf" name="rating" value="0.5" /><label class="half" for="{{$id}}starhalf" title="Sucks big time - 0.5 stars"></label>
    </fieldset>
</div>